import React, { memo } from 'react';
import { Link } from 'react-router-dom';

const truncate = (text) => {
  return text.length > 14 ? text.substring(0, 14) + '...' : text;
};

const postCard = memo(({ post }) => {
  const { image, text, id, tags } = post;
  return (
    <div className='p-2 d-inline-block w-100'>
      <div className='bg-white rounded shadow p-3 h-100'>
        <div className='border-bottom pb-2 mb-2 d-flex align-items-center'>
          <img
            className='box-32px rounded-circle'
            src='https://randomuser.me/api/portraits/women/93.jpg'
            alt='author'
          />
          <div className='ml-2'>
            <div className='h6 mb-0'>Emilie Lambert</div>
            <div className='text-muted small'>emilie.lambert@example.com</div>
          </div>
        </div>
        <img width='280' src={image} alt={text} width />
        <div className='d-flex my-2 mx-n1'>
          {tags.map((tag) => (
            <div key={`${tag}`} className='badge badge-pill badge-pink ml-1'>
              {tag}
            </div>
          ))}
        </div>
        <div className='h6 mt-2 mb-0 text-truncate'>{truncate(`${text}`)}</div>
        <div className='d-flex justify-content-between border-top pt-2 mt-2'>
          <div>22 Likes</div>
          <div className='text-muted text-right'>Apr 13 2020 11:36:18</div>
        </div>
        <div className='border-top mt-2 pt-2 text-left'>
          <div className='out-a small pointer text-underline left'>
            <Link className='nav-link out-a' to={`/profile/${id}`}>
              Full Profile
            </Link>
          </div>
          <div className='out-a small pointer text-underline right'>
            <Link
              className='nav-link out-a'
              to={`/profile/${id}`}
              to='/profile/'
            >
              Posts List
            </Link>
          </div>
        </div>
      </div>{' '}
    </div>
  );
});

export default postCard;
