import React, { useEffect, useState } from 'react';
import { useQuery } from 'react-query';
import { getUserDetail } from '../../queries';


const profileDetail = () => {
    const [skip, setSkip] = useState(false);
    const { isLoading, error, data, refetch } = useQuery(
        'User',
        getUserDetail,
        { skip },
    );

    useEffect(() => {
        if (!isLoading && data) {
          setSkip(true);
        }
      }, [data, isLoading]); // eslint-disable-line react-hooks/exhaustive-deps

    if (error) {
        return <div>There was an Error {error}</div>;
      }
      if (!data) {
        return <div onClick={() => refetch()}>Loading....</div>;
      }

    return (
      <div>
        <img src={data.picture} className="w-100 mw-240px mb-3" alt="dummmy user" />
        <div className="col-12 col-md-4">
            <div className="h5 whte-space">{data.firstName}</div>
            <div className="whte-space"><b>Gender: </b>Male</div>
            <div className="whte-space"><b>Date Of Birth: </b>Mar 13 1974</div>
            <div className="whte-space"><b>Register Date: </b>Mar 07 2020</div><br />
            <div className="whte-space"><b>Email: </b>heinz-georg.fiedler@example.com</div>
            <div className="whte-space"><b>Phone: </b>0700-3090279</div>
          </div>
      </div>
    );
  };

  profileDetail.displayName = 'profileDetail';

  export default profileDetail;
