import { createStore } from 'redux';

export const ACTIONS = {
    SET_ALL_AUTHORS: 'SET_ALL_AUTHORS',
    FILTER_AUTHORS: 'FILTER_AUTHORS',
};

const initialState = {
    authors: [],
    api_authors: [],
};

function authorReducer(state = initialState, action) {
  switch (action.type) {
    case ACTIONS.SET_ALL_AUTHORS: {
      const { authors } = action.payload;
      return {
        ...state,
        authors,
        api_authors: authors,
      };
    }
    case ACTIONS.FILTER_AUTHORS: {
        const { search_keys } = action.payload;
        let authors = state.api_authors ;
        if(search_keys.title !== 'All'){
            authors = state.api_authors.filter((item) => item.title === search_keys.title);
        }
        if(search_keys.firstName){
            authors = state.api_authors.filter((item) => item.firstName.includes(search_keys.firstName));
        }
        if(search_keys.lastName){
            authors = state.api_authors.filter((item) => item.lastName.includes(search_keys.lastName));
        }
        if(search_keys.email){
            authors = state.api_authors.filter((item) => item.email.includes(search_keys.email));
        }
        console.log("-------------")
        console.log(authors);
        console.log(state.api_authors);
        console.log(search_keys);
        console.log("-------------")

        return {
          ...state,
          authors,
        };
      }
    default:
      return state;
  }
}

const enableReduxDevTools = window.__REDUX_DEVTOOLS_EXTENSION__?.();

export function createReduxStore() {
  const store = createStore(authorReducer, enableReduxDevTools);
  return store;
}
