import React, { useEffect, lazy } from "react";
import { getMoviesList } from "../../queries";

import { useQuery } from "react-query";
import { useSelector, useDispatch } from 'react-redux';

import { ACTIONS } from '../../redux';


const MovieList = lazy(() => import("../../components/movie-card"));

const MovieListingView =  () => {
  const [skip, setSkip] = React.useState(false)
  const { isLoading, error, data} = useQuery('Authors', getMoviesList, { skip });
  const authors = useSelector((state) => state.authors);
  const dispatch = useDispatch();

  console.log("---------loading------");
  console.log(isLoading);
  console.log(data);
  console.log("---------loading------");


  useEffect(() => {
    // check whether data exists

  console.log("---------loading------");
  console.log(isLoading);
  console.log(data);
  console.log("---------loading------");

    if (!isLoading && data && data.data) {
      setSkip(true);
      dispatch({
        type: ACTIONS.SET_ALL_AUTHORS,
        payload: {
          authors: data.data
        },
      });
    }
  }, [skip])

  if (error) {
    return <div>There was an Error {error}</div>;
  }
  if (!data) {
    return <div>Loading....</div>;
  }


  console.log("---------authors------");
  console.log(authors);
  console.log("---------authors------");


  return (
    <div>
      {authors.map((userDetails) => (
        <MovieList key={`${userDetails.firstName}-${userDetails.lastName}`} user={userDetails} />
      ))}
    </div>
  );
};

MovieListingView.displayName = "MovieListingView";

export default MovieListingView;
