import React, { useEffect, lazy } from 'react';
import { useQuery } from 'react-query';
import { getUserDetail } from '../../queries';


const userDetail = () => {
    const [skip, setSkip] = React.useState(false);
    const { isLoading, error, data, refetch } = useQuery(
        'User',
        getUserDetail,
        { skip },
    );

    useEffect(() => {
        if (!isLoading && data) {
          setSkip(true);
        }
      }, [data, isLoading]); // eslint-disable-line react-hooks/exhaustive-deps

    return (
      <div>
        <img src="https://randomuser.me/api/portraits/men/81.jpg" className="w-100 mw-240px mb-3" alt="dummmy user" />
        <div className="col-12 col-md-4">
            <div className="text-muted">0F8JIqi4zwvb77FGz6Wt</div>
            <div className="h5 whte-space">mr. Heinz-Georg Fiedler</div>
            <div className="whte-space"><b>Gender: </b>Male</div>
            <div className="whte-space"><b>Date Of Birth: </b>Mar 13 1974</div>
            <div className="whte-space"><b>Register Date: </b>Mar 07 2020</div><br />
            <div className="whte-space"><b>Email: </b>heinz-georg.fiedler@example.com</div>
            <div className="whte-space"><b>Phone: </b>0700-3090279</div>
          </div>
      </div>
    );
  };

  export default userDetail;
