import React,{ memo } from 'react';
import { Link } from "react-router-dom";


const truncate = (text) => {
  return text.length > 14 ? text.substring(0, 14) + "..." : text;
}


const postCard = memo(({ post }) => {
  const { image, text, id, tags } = post;
  return (
