    <div className="p-2 d-inline-block w-100">
        <div className="bg-white rounded shadow p-3 h-100">
            <div className="border-bottom pb-2 mb-2 d-flex align-items-center">
              <img className="box-32px rounded-circle" src="https://randomuser.me/api/portraits/women/93.jpg" alt="author" />
               <div className="ml-2">
                  <div className="h6 mb-0">Emilie Lambert</div>
                   <div className="text-muted small">emilie.lambert@example.com</div>
              </div>
              </div>
              <img  width="280" src={image} alt={text} width/>
                <div className="d-flex my-2 mx-n1">
                {tags.map((tag) => (
   <div key={`${tag}`} className="badge badge-pill badge-pink ml-1">{tag}</div>
       ))}
                  </div>Adult Labrador retriever<br />
