                    <a className="out-a" target="_blank" rel="noopener noreferrer external nofollow" href="https://www.instagram.com/teddyosterblomphoto/">https://www.instagram.com/teddyosterblomphoto/</a>
                    </div>
                    <div className="d-flex justify-content-between border-top pt-2 mt-2">
                      <div>43 Likes</div>
                      <div className="text-muted text-right">May 24 2020 20:23:17</div>
                      </div>
                      <div className="border-top mt-2 pt-2 text-left">
                        <div className="out-a small pointer text-underline" role="button">Get Post Comments</div>
                        <div className="out-a small pointer text-underline" role="button">Get Owner Profile</div>
                        </div></div></div>
  );
});

export default postCard;
